import React, { useState, useEffect } from 'react';
import './App.css';
import StudentsList from './components/studentsList/studentsList';
import ls from 'local-storage';
import { useSelector, useDispatch } from 'react-redux';



function App() {
  const student = useSelector((state) => state.student);
  let studentArray = useSelector((state) => state.studentArray)
  const dispatch = useDispatch();



  return (
    <div className="App">
        <form className="add-student-container">
          <input onChange={((e) => dispatch({type:'CHANGE_NAME', peyload: e.target.value}))} id="studant-name" placeholder="Add your name, more than 2 character" type="text"/>
          <input onChange={((e) => dispatch({type:'CHANGE_AGE', peyload: e.target.value}))} id="student-age" placeholder="Add your Age 18+" min='18' max='130' type="number"/>
          <select onChange={((e) => dispatch({type:'CHANGE_PROGRESS', peyload: e.target.value}))} id="student-progress">
            <option>Wery good</option>
            <option>Good</option>
            <option>Normal</option>
            <option>Bad</option>
          </select>
          <button onClick={((e)=> {e.preventDefault();
             dispatch({type:'ADD_NEW_STUDENT', peyload: student})})} id="add-new-student" type="submit">Submit</button>
        </form>
        {studentArray.map((student, index) => (
          <StudentsList 
            name={student.name}
            age={student.age}
            progress={student.progress}
            id={index}
          />
        ))}
    </div>
  );
}


export default App;
