import React, { useState, useEffect } from 'react';
import './App.css';
import StudentsList from './components/studentsList/studentsList';
import ls from 'local-storage';
import { useSelector, useDispatch } from 'react-redux';

let id = 0;

function App() {
  const [studentName, addStudentName] = useState('');
  const [studentAge, addStudentAge] = useState('');
  const [studentProg, addStudentProg] = useState('Wery Good');
  const [hideStudent, changeStudentHiding] = useState({
    hiding:true,
    id: hideStudent.id
  });
  const studentList = useSelector((state) => state.studentList);
  const dispatch = useDispatch();
  let myStudents = JSON.parse(ls.get('student'));



  useEffect(() => {
    dispatch({
      type: 'ADD_NEW_STUDENT',
      payload: myStudents
    })
  }, []);

  useEffect(() => {
    ls.set('student', JSON.stringify(studentList));
  }, [myStudents, studentList]);

  const submitStudent = (e) => {
      e.preventDefault();
        dispatch({
          type: 'ADD_NEW_STUDENT',
          payload: {
            id: id,
            name: studentName,
            age: studentAge,
            prog: studentProg
          }
        });
        id++;
  }



  return (
    <div className="App">
        <form className="add-student-container">
          <input onChange={event => addStudentName(event.target.value)}  id="studant-name" placeholder="Add your name, more than 2 character" type="text"/>
          <input onChange={event => addStudentAge(event.target.value)}  id="student-age" placeholder="Add your Age 18+"  type="date"/>
          <select  onChange={event => addStudentProg(event.target.value)} id="student-progress">
            <option>Wery good</option>
            <option>Good</option>
            <option>Normal</option>
            <option>Bad</option>
          </select>
          <button onClick={submitStudent} id="add-new-student" type="submit">Submit</button>
        </form>
        {studentList.map((student, index) => (
         <StudentsList 
         id={student.id}
         name={student.name}
         age={student.age}
         prog={student.prog}
         hideStudent={hideStudent}
         changeStudentHiding={changeStudentHiding}
         />
        ))}
    </div>
  );
}





export default App;
