import React, { useState } from 'react';
import { useDispatch } from 'react-redux';

function StudentsList({name, age, prog, id}){
    const [updateName, updateStudentName] = useState(name);
    const [updateAge, updateStudentAge] = useState(age);
    const [updatepPog, updateStudentProg] = useState(prog);
    const dispatch = useDispatch();

    const [hideStudent, changeStudentHiding] = useState({
        id: id,
        hiding: true
    });

    const editStudentInfo = (e) => {
        e.preventDefault();
        changeStudentHiding(false);
      }

      const deleteStudent = (e) => {
        e.preventDefault()
        dispatch({
            type: 'DELETE_STUDENT',
            payload: {
              id: id,
            }
          });
          console.log(id,'deleteid')
      }

      const updateStudentInfo = (e) => {
        e.preventDefault();
        changeStudentHiding({
            id: id,
            hiding: true
        });
        console.log(id,'id')
        dispatch({
            type: 'UPDATE_STUDENT',
            payload: {
              id: id,
              name: updateName,
              age: updateAge,
              prog: updatepPog
            }
          });

      }
    return (
     <form id={id}  className={(hideStudent.hiding && hideStudent.id === id ? 'student-list' : '') }>
         <input onChange={event => updateStudentName(event.target.value)} type="text" value={updateName}/>
         <input onChange={event => updateStudentAge(event.target.value)} max="2001-01-01"  type="date" value={updateAge}/>
         <select onChange={event => updateStudentProg(event.target.value)} value={updatepPog}>
            <option>Wery good</option>
            <option>Good</option>
            <option>Normal</option>
            <option>Bad</option>
         </select>
         <div className="buttons-container">
            <button onClick={editStudentInfo} className="edit-student-info">Edit</button>
            <button onClick={deleteStudent} className="delete-student">Delete</button>
         </div>
         <input onClick={updateStudentInfo} className="add-edits" type="submit" value="Submit"/>
     </form>
    )
}
export default StudentsList;
