import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import ReactDOM from "react-dom";

const node = ReactDOM.findDOMNode(this);

function AddStudent(){
  const student = useSelector((state) => state.student);
  const dispatch = useDispatch();

function submitStudent(e){
  const inputName = node.querySelector('#studant-name')
  e.preventDefault();
  if(inputName.value <= 2){
    inputName.style.borderColor = 'red';
  }
  dispatch({type:'ADD_NEW_STUDENT', peyload: student});
}

    return (
        <form onSubmit={submitStudent} className="add-student-container">
          <input onChange={((e) => dispatch({type:'CHANGE_NAME', peyload: e.target.value}))} id="studant-name" placeholder="Add your name, more than 2 character" type="text"/>
          <input onChange={((e) => dispatch({type:'CHANGE_AGE', peyload: e.target.value}))} id="student-age" placeholder="Add your Age 18+" min='18' max='130' type="number"/>
          <select onChange={((e) => dispatch({type:'CHANGE_PROGRESS', peyload: e.target.value}))} id="student-progress">
            <option>Wery good</option>
            <option>Good</option>
            <option>Normal</option>
            <option>Bad</option>
          </select>
          <input id="add-new-student" type="submit" value="Submit"/>
       </form>
    )
}

export default AddStudent;
