import ls from 'local-storage';

const initialState = {
    name: '',
    age: '',
    progress: 'Wery Good',
    editable: false,
    submit: false,
    usersArray: []
}


export default function addNewStudent(state = initialState, action){
    switch(action.type){
        case 'CHANGE_NAME':
        state = {
            ...state,
            name: action.peyload
        }
        break;
        case 'CHANGE_AGE':
            state = {
                ...state,
                age: action.peyload
            }
        break;
        case 'CHANGE_PROGRESS':
            state = {
                ...state,
                progress: action.peyload
            }
        break;
        case 'ADD_NEW_STUDENT':
            state = {
                ...state,
                submit: true
            }
            usersArray.push(state);
            ls.set('student', JSON.stringify(state));
            break;
        default: 
        state = {...state}
    }

    return state;
}
