import React from 'react';
import { useSelector, useDispatch } from 'react-redux';

function AddStudent({addNewStudent , chnageName, changeAge, changeProgress}){
  const student = useSelector((state) => state.student)
  const dispatch = useDispatch();
    return (
        <form onSubmit={addNewStudent} className="add-student-container">
          <input onChange={(() => dispatch({type:'CHANGE_NAME', peyload: student.name}))} id="studant-name" placeholder="Add your name, more than 2 character" type="text"/>
          <input onChange={changeAge} id="student-age" placeholder="Add your Age 18+" type="number"/>
          <select onSelect={changeProgress} id="student-progress">
            <option>Wery good</option>
            <option>Good</option>
            <option>Normal</option>
            <option>Bad</option>
          </select>
          <input id="add-new-student" type="submit" value="Submit"/>
       </form>
    )
}

export default AddStudent;
