import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import ls from 'local-storage';




function AddStudent(){
  const student = useSelector((state) => state.student)
  const dispatch = useDispatch();
  function addNewStudent(e){
    e.preventDefault();
    dispatch({type:'ADD_NEW_STUDENT', peyload: student});
    let local = student;
    ls.set('student', JSON.stringify(student));

  }

    return (
        <form onSubmit={addNewStudent} className="add-student-container">
          <input onChange={((e) => dispatch({type:'CHANGE_NAME', peyload: e.target.value}))} id="studant-name" placeholder="Add your name, more than 2 character" type="text"/>
          <input onChange={((e) => dispatch({type:'CHANGE_AGE', peyload: e.target.value}))} id="student-age" placeholder="Add your Age 18+" type="number"/>
          <select onChange={((e) => dispatch({type:'CHANGE_PROGRESS', peyload: e.target.value}))} id="student-progress">
            <option>Wery good</option>
            <option>Good</option>
            <option>Normal</option>
            <option>Bad</option>
          </select>
          <input id="add-new-student" type="submit" value="Submit"/>
       </form>
    )
}



export default AddStudent;
