import ls from 'local-storage';

const initialState = {
    studentList: []
};

export default function addNewStudent(state = initialState, action){
    switch(action.type){
        case 'ADD_NEW_STUDENT':

            state = {
                ...state,
                studentList: state.studentList.concat(action.payload)
            }
            console.log(state)
            ls.set('student', JSON.stringify(state.studentList));

            break;
        case 'UPDATE_STUDENT' :
            const student = action.payload
            const updateStudent = state.studentList[student.id];
            updateStudent.name = student.name;
            updateStudent.age = student.age;
            updateStudent.prog = student.prog;
            console.log(state)
            break;
        case 'DELET_STUDENT' : 
        state.studentList[action.payload.id].delete();
        default: 
        state = {...state}
    }

    return state;
}
